import Vue from "vue";
import Vuex from "vuex";
//import createPersistedState from "vuex-persistedstate";

import notificationStore from "./notifications";
import loadingStore from "./loading";

// stores locais
import config from "./config";
import auth from "./auth";
import anon from "./anon";
import cargos from "./cargos";
import empresas from "./empresas";
import marcas from "./marcas";
import setores from "./setores";
import updates from "./updates";
import error from "../pages/error/stores";
import mainerror from "./error";
import producaoStore from "./producao";

// stores das paginas
import transpStore from "../pages/transp/stores";
import consultasStore from "../pages/consulta/stores";
import producaoidelliStore from "../pages/producaoidelli/stores";
import covidStore from "../pages/covid/stores";
import AlmoxarifadoStore from "../pages/almoxarifado/stores";
import coletaStore from "../pages/coleta/stores";
// import firebaseStore from "./firebasek1";
import chatStore from "../pages/chat/stores";
import ramaisStore from "../pages/ramais/stores";
import mapaestoqueStore from "../pages/MapaEstoque/stores";
import lotesproducaoStore from "../pages/lotesproducao/stores";
import pedidosacessoriosStore from "../pages/pedidosacessorios/stores";
import caixasaidaStore from "../pages/caixasaida/stores";
import adminStore from "../pages/admin/stores";
import gestoresStore from "../pages/gestores/stores";
import cartaopontoStore from "../pages/funcionarios/stores";

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    // informações sobre o sistema ...
    system: {
      app: {
        devmode: process.env.NODE_ENV === "development",
        majorver: 1,
        buildver: 36,
        correctver: 0,
        // version: '1.1.0',
        updatedate: new Date("2022-09-26 07:10"),
        author: {
          name: "Marcelo Horlle",
          email: "marcelo.info@grupok1.com.br",
        },
        features: [
          {
            modules: ["MapaEstoque"],
            updatedate: new Date("2021-07-19 07:26"),
            problem: "Mapeamento do estoque de materiais",
            solution: "Criado sistema de desenho de box para alocar produtos.",
          },
          {
            modules: ["Menu empresa"],
            updatedate: new Date("2021-07-21 22:34"),
            problem: "Definir empresa padrão",
            solution: "Criado o menu empresas no mainnav.",
          },
          {
            modules: ["Almoxarifado"],
            updatedate: new Date("2021-07-23 13:15"),
            problem: "Não filtrava pela separação correta",
            solution: "estava pegando o id errado ao passar para a API",
          },
          {
            modules: ["Almoxarifado"],
            updatedate: new Date("2021-07-24 15:24"),
            problem: "Erros de nomes dos campos",
            solution: "Ajustado erros campos e funcionalidades",
          },
          {
            modules: ["GaiolasItens"],
            updatedate: new Date("2021-07-29 17:01"),
            problem: "Ajustar a busca de itens para separação",
            solution:
              "Adicionado tabela com relação dos lotes e quenatidades para separação",
          },
          {
            modules: ["E-Commerce"],
            updatedate: new Date("2021-08-16 11:00"),
            problem: "Nova tela de pedidos E-commerce",
            solution:
              "Adicionado a tela para acompanhamento de pedidos do E-commerce",
          },
          {
            modules: ["Almoxarifado"],
            updatedate: new Date("2021-10-20 00:04"),
            problem:
              "Correções funcionalidades, adicionado consulta de estoque pelo mapa",
            solution: "",
          },
          {
            modules: ["App"],
            updatedate: new Date("2021-10-24 00:12"),
            problem: "Reposicionar páginas",
            solution:
              "Ao expirar o prazo de conexão reposicionar na última página selecionada.",
          },
          {
            modules: ["GridGallery", "Sidemenu"],
            updatedate: new Date("2021-10-29 20:12"),
            problem: "Sem tradução",
            solution: "Adicionado traduções para os menus do sistema.",
          },
          {
            modules: ["Usuarios"],
            updatedate: new Date("2021-11-09 13:06"),
            problem: "Sem tradução",
            solution: "Adicionado traduções para os menus do sistema.",
          },
          {
            modules: ["AlmoxarifadoMovimentacoes"],
            updatedate: new Date("2022-02-09 15:06"),
            problem: "Implementar movimentação",
            solution: "Criado rotina para busca a movimentação de estoque.",
          },
          {
            modules: ["Visitantes"],
            updatedate: new Date("2022-02-09 15:06"),
            problem: "Implementar triagem de visitantes",
            solution:
              "Convertido o sistema de verificação da COVID de visitantes do sistema antigo.",
          },
          {
            modules: ["AlmoxarifadoMovimentacoes"],
            updatedate: new Date("2022-02-13 18:06"),
            problem:
              "Ao adicionar um item pesquisado nas tags o sistena não adicionava na variavel correta",
            solution: "Ajuste busca de itens nas tags.",
          },
          {
            modules: ["MenuGallery"],
            updatedate: new Date("2022-02-13 18:06"),
            problem: "Necessidade de não apresentar a tela de boas-vindas",
            solution:
              "Adicionado uma propriedade show e um slot header se necessitar adicionar outro html no header.",
          },
          {
            modules: ["FichaTécnica"],
            updatedate: new Date("2022-02-18 13:06"),
            problem: "implantar a ficha técnica de produtos",
            solution: "Adicionado a rota para fichatecnica ainda em BETA.",
          },
          {
            modules: ["Despesas"],
            updatedate: new Date("2022-02-18 13:06"),
            problem: "implantar os lançamentos de despesa",
            solution: "Adicionado a rota para despesas ainda em BETA.",
          },
          {
            modules: ["Ramais"],
            updatedate: new Date("2022-04-01 13:06"),
            problem: "adicionar historico de ligações",
            solution:
              "Criado telas para cadastrar mensagens para as ligações de ramais.",
          },
          {
            modules: ["Almoxarifado"],
            updatedate: new Date("2022-04-01 13:06"),
            problem: "falta um botão para criar uma manutenção",
            solution: "Adicionado o botão.",
          },
          {
            modules: ["QRCodes", "Qr_codes_imob"],
            updatedate: new Date("2022-05-03 20:17"),
            problem: "Não imprime os qrcodes",
            solution: "Erro na atribuição do botão ok da janela modal.",
          },
          {
            modules: ["Carregamento"],
            updatedate: new Date("2022-05-25 15:41"),
            problem: "Coletar assinatura dos motoristas",
            solution: "Criado a tela Carregamento dentro da aba Expedição.",
          },
          {
            modules: ["Carregamento"],
            updatedate: new Date("2022-05-26 11:07"),
            problem: "Salvar as assinaturas apenas das cargas 100% coletadas",
            solution: "Criado salvamento de assinaturas e adicionado filtros de assinados e coletados para facilitar as buscas.",
          },
          {
            modules: ["Authentication"],
            updatedate: new Date("2022-09-26 07:10"),
            problem: "Solicita login para a autenticação anônima ao abrir a página inicial",
            solution: "Renovar a autenticação anônima quando vencida.",
          },
        ],
        bootstrap: {
          buttons: {
            size: "sm",
          },
        },
      },
    },

    // mensagens de alertas do sistema
    alert: {
      visible: false,
      title: "Intranet",
      variant: "Info",
      message: "",
    },

    // armazena a rota para o redirecionamento ...
    guard: {
      login: false,
      route: {
        path: "/",
      },
    },

    loading: {
      visible: false,
      message: "Por favor aguarde!",
    },
  },

  getters: {
    sysapp: (state) => state.system.app,
    sysversion: (state) =>
      state.system.app.majorver.toString() +
      "." +
      state.system.app.buildver.toString() +
      "." +
      state.system.app.correctver.toString(),
    sysupdatedate: (state) =>
      state.system.app.updatedate.toLocaleDateString() +
      " " +
      state.system.app.updatedate.toLocaleTimeString(),
    sysauthor: (state) => state.system.app.author,
    sysfeatures: (state) => state.system.app.features,
    sysdevmode: (state) => state.system.app.devmode,

    sysdate: () => {
      const dt = new Date();
      const wd = ["Dom", "Seg", "Ter", "Qua", "Qui", "Sex", "Sáb"];
      const hoje = {
        date: dt,
        week: dt.getDay() - 1,
        weekext: wd[dt.getDay() - 1],
        day: dt.getDate(),
        month: dt.getMonth() + 1,
        year: dt.getFullYear(),
      };
      return hoje;
    },

    // alerts
    gtxalert: (state) => state.alert,

    // guards
    gtxguardroute: (state) => state.guard.route.path,

    gtxLoading: (state) => state.loading.message,
    gtxLoadingVisible: (state) => state.loading.visible,

    gtxuserbyid: (state, idusuario) => {
      state.users.find((f) => (f.id = idusuario));
    },
  },

  mutations: {
    setfeatures: (state, feature) => {
      state.system.app.features = feature;
    },
    /*
        setLoading: (state, loading) => {
            state.loading.message = loading
        },
        */
    setLoadingVisible: (state, visible) => {
      state.loading.visible = visible;
    },
    setAlert: (state, alert) => {
      if (!alert.title) alert.title = "Intranet";
      alert.visible = true;
      state.alert = alert;
    },
    setAlertVisible: (state, visible) => {
      state.alert.visible = visible;
    },
    setGuardRoute: (state, route) => {
      state.guard.route = route;
    },
  },

  actions: {
    actAlert: ({ commit }, alert) => {
      const value = {
        visible: true,
        title: "Intranet",
        variant: "Info",
        message: "",
      };
      if (typeof alert == "string") {
        value.message = alert;
      }
      if (typeof alert == "object") {
        (value.title = alert.title || "Intranet"),
          (value.variant = alert.variant || "Info"),
          (value.message = alert.message);
      }
      commit("setAlert", alert);
    },

    actAlertVisible: ({ commit }, visible) => {
      commit("setAlertVisible", visible);
    },

    actLoadingVisible: ({ commit }, visible) =>
      commit("setLoadingVisible", visible),

    actToast({ commit }, payload) {
      const toast = {
        message: "",
        icon: "info-square",
        title: "Atenção",
        variant: "info",
        appendToast: true,
      };

      if (typeof payload == "string") {
        toast.message = payload;
      }

      if (typeof payload == "object") {
        toast.message = payload.message;
        toast.icon = payload.icon || "info-square";
        toast.variant = payload.variant || "info";
        toast.append = payload.append || true;
        toast.title = payload.title || "Atenção";
      }
      commit("setAlert", toast);
    },
  },

  // modulos da store ...
  modules: {
    notificationStore,
    loadingStore,
    // stores locais
    config,
    auth,
    anon,
    cargos,
    empresas,
    marcas,
    setores,
    updates,
    mainerror,
    error,
    producaoStore,

    // stores das pages
    transpStore,
    consultasStore,
    producaoidelliStore,
    covidStore,
    AlmoxarifadoStore,
    coletaStore,
    // firebaseStore,
    chatStore,
    ramaisStore,
    mapaestoqueStore,
    lotesproducaoStore,
    pedidosacessoriosStore,
    caixasaidaStore,
    adminStore,
    gestoresStore,
    cartaopontoStore,
  },
});
