/*

o formato do armazenamento das stores deverá ser criando um objeto com o nome do login
com os dados de autenticação, estado ativo ou inativo, token de acesso e objeto storedata
contendo todas os dados já coletados da API para possibilitar o trabalho offline

{
    "spr_repres": {
        auth: true,
        lastlogin: "2020-11-16T16:26:25.381Z",
        token: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXV..."
        storedata: [
            {store: "empresas",
                lastupdate: "2020-11-16T16:26:25.381Z",
                data: [
                    {value: 1, text: 'Filial'},
                    {value: 8, text: 'Matriz'},
                ]
            },
            {store: "marcas",
                lastupdate: "2020-11-16T16:26:25.381Z",
                data: [
                    {value: 1, text: 'Kappesberg'},
                    {value: 2, text: 'Idelli'},
                ]
            }
        ],
        storepage: [
            {store: "partlist",
                lastupate: "2020-11-16T16:26:25.381Z",
                
        ]
    }
}

*/

import JwtService from '../services/jwt.service'

export default {

    name: 'LocalStore',
    username: JwtService.getUserName(),

    // store completa do login ...
    getUserStore() {

        if (!this.username) {
            // console.error('Usuário não autenticado!')
            return null
        }

        // console.log('localstore.getUserStore: ', 'hit')
        // console.log('localstore.userStore: ', this.username)
        const userStore = localStorage.getItem(this.username)

        // se existe retorna, se não monta uma estrutuda padrão ...
        return (userStore) ? JSON.parse(userStore) : {
            auth: false,
            token: null,
            lastLogin: null,
            lastLogout: null,
            loading: false,
            storedata: [],
            storepage: []
        }
        
    },

    // pega todas as stores do login ...
    getUserStoreData() {

        const userStore = this.getUserStore()
        // console.log('localstore.getUserStoreData: ', userStore)
        return (userStore) ? userStore.storedata : null

    },

    // pega uma store pelo nome ...
    getUserStoreDataByName(storename) {

        // console.log('localstore.getUserStoreDataByName: ', storename)
        
        const storeData = this.getUserStoreData()
        // console.log('localstore.getUserStoreDataByName().storeData: ', storeData)
        return (storeData) ? storeData.find(s => s.store == storename) : null

    },

    // store das páginas ...
    // pega todas as stores das páginas do login ...
    getUserStorePageData() {

        const userStore = this.getUserStore()
        // console.log('localstore.getUserStoreData: ', userStore)
        return (userStore) ? userStore.storepage || [] : []

    },
    
    // pega uma store de páginas pelo nome ...
    getUserStorePageByName(storepage) {

        const storeData = this.getUserStorePageData()
        // console.log('localstore.getUserStoreDataByName().storeData: ', storeData)
        return (storeData) ? storeData.find(s => s.store == storepage) || {} : {}

    },

    // setters ...
    // salva a store no localstore ...
    setUserStoreData(storename, data) {
        
        const userStore = this.getUserStore() || {storedata: []}
        let storeData = this.getUserStoreDataByName(storename)
        // .log('localstore.setUserStoreData.storeData: ', storeData)

        if (!storeData) {
            storeData = {store: storename, data: data}
            userStore.storedata.push(storeData)
        } else {
            storeData.data = data
        }
        
        // sempre atualiza essa data para informar que está atualizado
        storeData.updatedate = new Date()

        // salva o novo estado da store no localstorage ...
        localStorage.setItem(this.username, JSON.stringify(userStore))

    },

    // salva a store no localstore ...
    setUserStorePageData(pagename, data) {
        
        // console.log('localstore.setUserStoreData.storename: ', storename)
        // console.log('localstore.setUserStoreData.data: ', data)
        
        const userStore = this.getUserStore()
        let storeData = this.getUserStorePageByName(pagename)
        // .log('localstore.setUserStoreData.storeData: ', storeData)

        if ((!storeData) || (!userStore.hasOwnProperty('storepage'))) {
            storeData = { store: pagename }
            if (!userStore.hasOwnProperty('storepage')) {
                userStore.storepage = []
            }
        }

        storeData.data = data

        let found = userStore.storepage.find(f => f.store == pagename)
        if (!found) {
            userStore.storepage.push(storeData)
        } else {
            found.data = data
        }

        // salva o novo estado da store no localstorage ...
        localStorage.setItem(this.username, JSON.stringify(userStore))

    },

    // busca todo o json do login autenticado ...
    getLoginStore() {

        const loginname = JwtService.getUserName()

        // se não consegue pegar o nome do login é pq não está logado ...
        if (!loginname) {

            return null

        } else {

            const data = localStorage.getItem(loginname)
    
            const jsondata = JSON.parse(data)
    
            // pega a store intera do login
            return jsondata ? jsondata : null

        }

    },

    // busca toda a store do login autenticado ...
    getStoreData() {

        const loginname = JwtService.getUserName()

        // se não consegue pegar o nome do login é pq não está logado ...
        if (!loginname) {

            return null

        } else {

            // pega a store intera do login
            const store = JSON.parse(localStorage.getItem(loginname))
            if (store && !store.storedata) store.storedata = []

            return store ? store.storedata : null

        }


    },

    // salva o dado no localstorage pelo nome passado no atributo "storedata" ...
    setlocalStore(storename, data) {

        const loginname = JwtService.getUserName()

        console.log('storename: ', storename)
        console.log('data: ', data)

        // se não consegue pegar o nome do login é pq não está logado ...
        if (!loginname) {

            return null

        } else {

            // pega a store inteira do login
            const store = JSON.parse(localStorage.getItem(loginname))
    
            // se ainda não carregou nenhuma store no login cria agora
            if (!store.storedata) store.storedata = []
    
            if (store) {
                // acha a store pelo nome passado...
                if (!store.storedata.find(s => s.store == storename)) {
                    store.storedata.push({store: storename, data: data})
                    this.savelocalStore(null, store)
                }
            }

        }

    },

    // salva o dado no localstore ...
    savelocalStore(storename, data) {

        const loginname = JwtService.getUserName()

        if ((!storename) || (storename == '')) storename = loginname

        if (storename !== '') localStorage.setItem(storename, JSON.stringify(data))

    },

    loginAuthoff() {
       
        const loginname = JwtService.getUserName()

        const log = localStorage.getItem(loginname)
        if (log && log !== '') {
            if (JSON.parse(log)) {
                let objLog = JSON.parse(log)
                objLog.auth = false
                objLog.token = ''

                this.savelocalStore(log, objLog)
            }
        }
        }    
    
}
