import ApiService from '../../../services/api.service'

export default {

    state: {

        pedidosacessorios: {
            success: true,
            error: null,
            busy: false,
            apiresponse: null,
            data: []
        },

        pedidosacessoriosupdate: {
            success: true,
            error: null,
            busy: false,
            data: []
        }


    },

    mutations: {

        setPedidosAcessoriosBusy: (state, busy) => state.pedidosacessorios.busy = busy,
        setPedidosAcessoriosResponse: (state, data) => state.pedidosacessorios = data,

        setPedidosAcessoriosUpdate: (state, data) => state.pedidosacessoriosupdate = data,

    },

    getters: {

        gtxPedidosAcessoriosBusy: state => state.pedidosacessorios.busy,
        gtxPedidosAcessoriosResponse: state => state.pedidosacessorios.apiresponse,
        gtxPedidosAcessoriosSuccess: state => state.pedidosacessorios.success,
        gtxPedidosAcessoriosError: state => state.pedidosacessorios.error,
        gtxPedidosAcessoriosData: state => state.pedidosacessorios.data,
        gtxPedidosAcessoriosVolumes: (state, getters) => volume => getters.gtxPedidosAcessoriosData.find(f => f.Seq == volume) || {Volumes: []},
        gtxPedidosAcessoriosVolumesChecked: (state, getters) => volume => getters.gtxPedidosAcessoriosVolumes(volume).Volumes.filter(f => f.checked),
        // gtxPedidosAcessoriosDataEmpresas: (state, getters) => [...[{ value: null, text: '-- TODOS --' }], ...(getters.gtxPedidosAcessoriosData.map(e => { return { value: e.value, text: e.text}}) || [])],
        // gtxPedidosAcessoriosDataAlmox: (state, getters) => empresa => [...[{ value: null, text: '-- TODOS --' }], ...(getters.gtxPedidosAcessoriosData.find(e => e.value == empresa) || { almoxarifados: [] }).pedidosacessoriosarifados],
        // gtxPedidosAcessoriosDataSelected: state => state.pedidosacessorios.selected,

        gtxPedidosAcessoriosUpdateBusy: state => state.pedidosacessoriosupdate.busy,
        gtxPedidosAcessoriosUpdateSuccess: state => state.pedidosacessoriosupdate.success,
        gtxPedidosAcessoriosUpdateError: state => state.pedidosacessoriosupdate.error,
        gtxPedidosAcessoriosUpdateData: state => state.pedidosacessoriosupdate.data,

    },

    actions: {

        actPedidosAcessoriosLimpar: ({ commit }) => {
            commit('setPedidosAcessoriosResponse', ApiService.prepareData([]))
        },

        actPedidosAcessorios: ({ commit }, params) => {
            
            if (params && !params.hasOwnProperty('params')) {
                params = {
                    params: params
                }
            }
            
            // console.log('actTiposeparacaoalmox:', params)

            commit('setPedidosAcessoriosBusy', true)
            ApiService.setAuthHeader()
            ApiService.get('Producao/PedidosAcessorios', params)
                .then(apidata => {
                    commit('setPedidosAcessoriosResponse', ApiService.prepareData(apidata.data))
                    commit('setPedidosAcessoriosBusy', false)
                })
                .catch(() => {
                    commit('setPedidosAcessoriosBusy', false)
                })            

        },

        actPedidosAcessoriosUpdate: ({ commit }, body) => {

            if (body && !body.hasOwnProperty('body')) {
                body = {
                    body: body
                }
            }
            
            // console.log('actPedidosAcessoriosUpdate:', body)

            commit('setPedidosAcessoriosUpdateBusy', true)
            ApiService.setAuthHeader()
            ApiService.post('Producao/OpitensEANAcessorios/Update', body)
                .then(apidata => {
                    commit('setPedidosAcessoriosUpdateResponse', ApiService.prepareData(apidata.data))
                    commit('setPedidosAcessoriosUpdateBusy', false)
                })
                .catch(() => {
                    commit('setPedidosAcessoriosUpdateBusy', false)
                })
        }

    }

}
